export default {
  name: 'layout',
  data() {
    return {};
  },
  mounted() {
    // 判断是从pc端还是移动端进入 跳到不同的页面
    const clientWidth = window.screen.width;
    if (clientWidth > 750) {
      this.$router.replace({
        name: 'home'
      });
    } else {
      this.$router.replace({
        name: 'h5Home'
      });
    }
  }
};
